for (const item of items) {
  // Access the 'title' from the 'Loop Over Items' node
  let title = $('Loop Over Items').first().json.title;

  // Sanitize the title
  let sanitizedTitle = title
    .replace(/[/\?<>\\:*|"\.\r\n]/g, '-') // Replaces invalid characters, including periods and newlines, with a hyphen
    .replace(/^-+|-+$/g, ''); // Removes leading or trailing hyphens

  // Create a new property on the item with the sanitized title
  item.json.sanitizedTitle = sanitizedTitle;
}

return items;

// Function to clean the query and create the filename
function cleanQueryToFilename(query) {
    if (!query) {
        return 'default-output';
    }

    const lowerCaseQuery = query.toLowerCase().trim();

    // 1. Handle 'link:' operator (Example: link:https://spitalfieldslife.com/2024/01/31/at-beppes-cafe/)
    if (lowerCaseQuery.startsWith('link:')) {
        let urlPart = query.substring(5).trim();

        // Remove protocol (http/https)
        let pathSegment = urlPart.replace(/https?:\/\//, '');

        // Split by '/' and get the last part (e.g., 'at-beppes-cafe/')
        let lastSegment = pathSegment.split('/').filter(s => s).pop();

        // Remove file extensions (like .html) and remaining dots
        let cleanedSegment = lastSegment ? lastSegment.replace(/\.[a-z]{2,5}$/i, '') : '';

        // Replace any remaining non-alphanumeric with dashes
        cleanedSegment = cleanedSegment.replace(/[^a-z0-9-]/gi, '-'); 

        // Final cleanup: remove multiple dashes and trim
        return cleanedSegment.replace(/-{2,}/g, '-').replace(/^-|-$/g, '') || 'link-search';
    }

    // 2. Handle 'site:' operator (Example: site:https://spitalfieldslife.com)
    if (lowerCaseQuery.startsWith('site:')) {
        let urlPart = query.substring(5).trim();

        // Remove protocol (http/https), 'www.', and trailing slash
        urlPart = urlPart.replace(/https?:\/\//, '').replace(/^www\./, '').replace(/\/$/, '');

        // Split at the first dot and take the domain name (spitalfieldslife)
        let domainName = urlPart.split('.')[0];

        return domainName.toLowerCase();
    }

    // 3. Handle General Text Prompt (Example: beppes cafe london)
    // Replace all non-alphanumeric (except space) with dashes, remove multiple dashes, and trim.
    let cleanedText = lowerCaseQuery;
    // Replace all non-alphanumeric or space with dash
    cleanedText = cleanedText.replace(/[^a-z0-9\s]/g, '-'); 
    // Replace spaces with single dash
    cleanedText = cleanedText.replace(/\s+/g, '-');
    // Final cleanup: remove multiple dashes and trim
    cleanedText = cleanedText.replace(/-{2,}/g, '-').replace(/^-|-$/g, '');

    return cleanedText;
}

