const originalQuery = $input.first().json.chatInput;

// Process the query to get the clean filename base
const cleanedFilename = cleanQueryToFilename(originalQuery);

// Return the original item with the new field added
return [{
    ...$json,
    cleaned_filename: cleanedFilename
}];
